import './styles.css';
import TypeAhead from '../../typeahead';
import { useState, useRef } from 'react';

const Step2 = props => {
  const [inputValue, setInputValue] = useState('');
  const {selectedContinent, selectedCountries, setSelectedCountries} = props;
  const [filteredCountryList, setFilteredCountryList] = useState(selectedContinent.countries);
  const searchBox = useRef();
  const onTypeAheadAction = (search) => {
    search = search.toLowerCase();
    const filteredList = countryList.filter((data)=>{
      return data.continent.toLowerCase().indexOf(search) > -1;
    });
    setFilteredCountryList(filteredList);
  };
  const setCountriesData = (value) => {
    searchBox.current.value='';
    if(selectedCountries[value.name] != undefined)
      setSelectedCountries(previoudata => { delete previoudata[value.name]; return previoudata})
    else
      setSelectedCountries(previoudata => { previoudata[value.name] = value; return previoudata})
  }
  return (
    <div>
      <h2>Step2</h2>
      <TypeAhead inputValue={inputValue} onTypeAheadAction={onTypeAheadAction} searchBox={searchBox}></TypeAhead>
      <ul>
        {filteredCountryList && filteredCountryList.map(value => (
          <li key={value.name} onClick={() => setCountriesData(value)}>{value.name}</li>
        ))}
      </ul>
    </div>
  );
}
export default Step2;
