import './styles.css';
import TypeAhead from '../../typeahead';
import { useState, useRef } from 'react';

const Step1 = props => {
  const [inputValue, setInputValue] = useState('');
  const {continentData, selectedContinent, setSelectedContinent} = props;
  const [continentDataToBeDisplayed, setContinentDataToBeDisplayed] = useState(continentData)
  const searchBox = useRef();
  const onTypeAheadAction = (search) => {
    console.log(search);
  };
  const selectContinent = () => {
    searchBox.current.value='';
  }
  return (
    <div>
      <h2>Step1</h2>
      <TypeAhead inputValue={inputValue} onTypeAheadAction={onTypeAheadAction} searchBox={searchBox}></TypeAhead>
      {!selectedContinent && 
        <ul>
          {continentDataToBeDisplayed.map((data)=> (<li onClick={setSelectedContinent(data)}>{data.continent}</li>))}
        </ul> || 
        <div>
          You Selected
          <div>{selectedContinent}</div>
        </div>
        }
    </div>
  );
}
export default Step1;
